if exists(select 1 from app.tbl_AAA_Orders_temp)
update app.tbl_watermarktable
set last_modified_date = (
select max(modified_timestamp) from app.tbl_aaa_orders_temp) where table_name = 'app.tbl_aaa_orders_temp';
select @@ROWCOUNT

if exists(select 1 from app.tbl_AAA_Orders_temp)
update a
set a.is_processed =1 from app.tbl_aaa_master_customerorderdata_stg a
join app.tbl_AAA_Orders_temp b on a.materialnumber = b.artikelnummerkunde and b.kundennummer = a.customernumber and a.PageCount = b.Pagecount and a.modifiedtime >
(select last_modified_date from app.tbl_watermarktable where table_name = 'app.tbl_aaa_customerorderdata');
select @@ROWCOUNT
